import React, { useState, useEffect } from "react";
import { Howl } from "howler";

const NowPlayingCard = ({ song }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [howlInstance, setHowlInstance] = useState(null);

  useEffect(() => {
    // Create a Howl instance for the current song
    if (song) {
      const newHowl = new Howl({
        src: [song.url], // Song URL
        html5: true, // Enables HTML5 Audio for better compatibility
      });
      setHowlInstance(newHowl);

      // Clean up the Howl instance when the song changes
      return () => {
        if (howlInstance) {
          howlInstance.stop();
          howlInstance.unload();
        }
      };
    }
  }, [song]);

  const handlePlayPause = () => {
    if (!howlInstance) return;

    if (isPlaying) {
      howlInstance.pause();
      setIsPlaying(false);
    } else {
      howlInstance.play();
      setIsPlaying(true);
    }
  };

  return (
    <div className="now-playing-card">
      <h4 className="now-playing-title">Now Playing</h4>
      <img
        src={song.image}
        alt={song.title}
        className="now-playing-image"
        style={{ width: "220px", height: "120px", borderRadius: "10px" }}
      />
      <div className="song-info">
        <h4>{song.title}</h4>
        <p>{song.album}</p>
      </div>
      <div className="progress-bar">
        <span>0:00</span> {/* Static for now */}
        <input
          type="range"
          min="0"
          max="100"
          value={howlInstance ? (howlInstance.seek() / howlInstance.duration()) * 100 : 0}
          onChange={(e) => howlInstance.seek((e.target.value / 100) * howlInstance.duration())}
          className="slider"
        />
        <span>{song.time}</span> {/* Total song duration */}
      </div>
      <div className="controls">
        <button onClick={() => howlInstance.seek(howlInstance.seek() - 10)}>⏮️</button>
        <button onClick={handlePlayPause}>{isPlaying ? "⏸️" : "▶️"}</button>
        <button onClick={() => howlInstance.seek(howlInstance.seek() + 10)}>⏭️</button>
      </div>
    </div>
  );
};

export default NowPlayingCard;
