import React from 'react';

const NowPlayingCard = ({ song }) => {
  return (
    <div className="now-playing-card">
      <img
        src="path_to_your_album_cover.jpg" // Add a placeholder or dynamic image path
        alt={song.title}
        style={{ width: '50px', height: '100px', borderRadius: '5px', marginRight: '10px' }}
      />
      <div className="song-info">
        <h4>{song.title}</h4>
        <p>{song.album}</p>
        <div className="controls">
          <button>Prev</button>
          <button>Play</button>
          <button>Next</button>
        </div>
      </div>
    </div>
  );
};

export default NowPlayingCard;
