import React from 'react';

const NowPlayingCard = ({ song }) => {
  return (
    <div className="now-playing-card">
      <img
        src={song.image} // Dynamically use the image from the selected song
        alt={song.title}
        style={{
          width: '50px',
          height: '50px',
          borderRadius: '5px',
          marginRight: '10px',
        }}
      />
      <div className="song-info">
        <h4>{song.title}</h4>
        <p>{song.album}</p>
        <div className="controls">
          <button>Prev</button>
          <button>Play</button>
          <button>Next</button>
        </div>
      </div>
    </div>
  );
};

export default NowPlayingCard;
