import React, { useState } from 'react';
import SongTable from './SongTable';
import NowPlayingCard from './NowPlayingCard';

const App = () => {
  const [currentSong, setCurrentSong] = useState(null); // Manage the currently playing song

  const handleSongClick = (song) => {
    setCurrentSong(song); // Set the clicked song as the currently playing song
  };

  return (
    <div className="app">
      <SongTable onSongClick={handleSongClick} />
      {currentSong && <NowPlayingCard song={currentSong} />} {/* Show NowPlayingCard only if a song is selected */}
    </div>
  );
};

export default App;
