import React from 'react';

const NowPlayingCard = ({ song }) => {
  return (
    <div className="now-playing-card">
      <h4 className="now-playing-title">Now Playing</h4>
      <img
        src={song.image} // Use the image from the selected song
        alt={song.title}
        className="now-playing-image"
      />
      <div className="song-info">
        <h4>{song.title}</h4>
        <p>Michael Jackson</p>
      </div>
      <div className="progress-bar">
        <span>2:15</span> {/* Static for now, can be dynamic */}
        <input type="range" min="0" max="100" value="50" className="slider" />
        <span>{song.time}</span>
      </div>
      <div className="controls">
        <button>⏮️</button> {/* Previous */}
        <button>⏯️</button> {/* Play/Pause */}
        <button>⏭️</button> {/* Next */}
      </div>
    </div>
  );
};

export default NowPlayingCard;
